import '../App.css';
import { useState } from "react";

function App() {

  const [pixel, setPixel] = useState(0);
  const color = "white";


  const pixelColor = () => {

    if (pixel === 0) {
      color = "black";
      pixel = 1;
    } else if (pixel === 1) {
      color = "white"
      pixel = 0;
    }



  }

  return (
    <>
      <div className="pixel-area">
        <button 
        className="pixel" 
        onClick={() => {pixelColor()}}
        style={{"background":color}}></button>
      </div>
    </>
  );
}

export default App;
