  return (
    <>
      <div className="pixel-area">
        <button
          onClick={() => {
            btnClass ? setBtnClass(false) : setBtnClass(true);
          }}
          className={btnClass ? "btnClass clicked" : "btnClass"}
          >but
        </button>
      </div>
    </>
  );
}
