import '../App.css';
import { useState } from "react";

export default function Cell() {

  const [btnColor, setBtnColor] = useState("white");

  return (
    <button onClick={() => {btnColor == "white" ? setBtnColor("black") : setBtnColor("white")}} style={{ backgroundColor: btnColor}}></button>
  );
}
