import '../App.css';
import { useState } from "react";

export default function App() {

  const [btnColor, setBtnColor] = useState("white");

  return (
    <>
      <div className="pixel-area">

        <button
          onClick={() => {btnColor == "white" ? setBtnColor("black") : setBtnColor("white")}} style={{ backgroundColor: btnColor}}></button>
      </div>
    </>
  );
}
